CREATE TABLE IF NOT EXISTS `{prefix}_product_select` (`id` int(11) unsigned NOT NULL auto_increment,`select_id` int(11) unsigned NOT NULL,`type` varchar(20) collate utf8_general_ci NOT NULL,`topic` varchar(50) collate utf8_general_ci NOT NULL,`module_id` int(11) unsigned NOT NULL,PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;
