INSERT INTO `{prefix}_emailtemplate` (`module`, `email_id`, `language`, `from_email`, `copy_to`, `name`, `subject`, `detail`, `last_update`, `last_send`) VALUES ('product','1','th','','','อีเมลแจ้งรายละเอียดการสั่งซื้อและชำระเงิน (1)','รายละเอียดการสั่งซื้อ %WEBTITLE%','<style type="text/css">table.movie_detail {\r\nmin-width: 800px;\r\nborder: 1px solid #666;\r\n}\r\ntable.movie_detail th {\r\npadding: 5px;\r\nbackground-color: #666;\r\ncolor:#FFF;\r\n}\r\ntable.movie_detail td{\r\npadding: 5px;\r\nbackground-color: #FFF;\r\ntext-align: center;\r\n}\r\n* {\r\nfont-family: Tahoma;\r\nfont-size: 12px;\r\n}\r\ntable.movie_detail td.left {\r\ntext-align: left;\r\n}\r\ntable.movie_detail td.right{\r\ntext-align: right;\r\n}\r\n</style>\r\n<div style="padding: 40px; margin-right: 40px; display: block; background-color: rgb(247, 247, 247);">\r\n<table style="width: 100%; border-collapse: collapse;">\r\n	<tbody>\r\n		<tr>\r\n			<th style="border-width: 1px; border-style: none solid; border-color: rgb(59, 89, 152); padding: 5px; text-align: left; color: rgb(255, 255, 255); font-family: tahoma; font-size: 9pt; background-color: rgb(59, 89, 152);">รายละเอียดการสั่งซื้อ เลขที่ %ORDER%</th>\r\n		</tr>\r\n		<tr>\r\n			<td style="border-width: 1px; border-style: none solid solid; border-color: rgb(204, 204, 204) rgb(204, 204, 204) rgb(59, 89, 152); padding: 15px; line-height: 1.8em; font-family: tahoma; font-size: 9pt;">รายการสั่งซื้อของคุณ&nbsp;<strong>%FNAME% %LNAME%</strong><br>\r\n			&nbsp;\r\n			<table class="movie_detail">\r\n				<tbody>\r\n					<tr>\r\n						<th>รหัสสินค้า</th>\r\n						<th>รายการ</th>\r\n						<th>จำนวน</th>\r\n						<th>ราคา</th>\r\n					</tr>\r\n					<tr>\r\n						<td>%DETAIL%</td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n			<br>\r\n			จำนวนเงิน <strong>%PRICE%</strong> %UNIT%<br>\r\n			ส่วนลด <strong>%DISCOUNT%</strong> %UNIT%<br>\r\n			น้ำหนักสินค้า <strong>%WEIGHT%</strong> กรัม<br>\r\n			ค่าขนส่ง <strong>%TRANSPORT%</strong> %UNIT%<br>\r\n			รวมเป็นจำนวนเงินทั้งสิ้น <strong>%TOTAL%</strong> %UNIT%<br>\r\n			<br>\r\n			กรุณาชำระเงิน และกลับมาแจ้งการชำระเงินได้ที่ <a href="%URL%" rel="nofollow">%URL%</a><br>\r\n			<br>\r\n			ด้วยความขอบคุณ %WEBTITLE%</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="padding: 15px; color: rgb(153, 153, 153); font-family: tahoma; font-size: 8pt;">ข้อความนี้ถูกส่งมาโดยระบบอัตโนมัติเมื่อคุณได้ทำรายการกับเรา จากที่อยู่ IP : %IP% เมื่อ %TIME%</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n','1418899267','0');
INSERT INTO `{prefix}_emailtemplate` (`module`, `email_id`, `language`, `from_email`, `copy_to`, `name`, `subject`, `detail`, `last_update`, `last_send`) VALUES ('product','2','th','','','ใบเสนอราคา (2)','ใบเสนอราคา %WEBTITLE%','<style type="text/css">table.movie_detail {\r\nmin-width: 800px;\r\nborder: 1px solid #666;\r\n}\r\ntable.movie_detail th {\r\npadding: 5px;\r\nbackground-color: #666;\r\ncolor:#FFF;\r\n}\r\ntable.movie_detail td{\r\npadding: 5px;\r\nbackground-color: #FFF;\r\ntext-align: center;\r\n}\r\n* {\r\nfont-family: Tahoma;\r\nfont-size: 12px;\r\n}\r\ntable.movie_detail td.left {\r\ntext-align: left;\r\n}\r\ntable.movie_detail td.right{\r\ntext-align: right;\r\n}\r\n</style>\r\n<div style="padding: 40px; margin-right: 40px; display: block; background-color: rgb(247, 247, 247);">\r\n<table style="width: 100%; border-collapse: collapse;">\r\n	<tbody>\r\n		<tr>\r\n			<th style="border-width: 1px; border-style: none solid; border-color: rgb(59, 89, 152); padding: 5px; text-align: left; color: rgb(255, 255, 255); font-family: tahoma; font-size: 9pt; background-color: rgb(59, 89, 152);">ใบเสนอราคา ตามใบสั่งซื้อเลขที่ %ORDER%</th>\r\n		</tr>\r\n		<tr>\r\n			<td style="border-width: 1px; border-style: none solid solid; border-color: rgb(204, 204, 204) rgb(204, 204, 204) rgb(59, 89, 152); padding: 15px; line-height: 1.8em; font-family: tahoma; font-size: 9pt;">ตามที่คุณได้ทำการสั่งซื้อสินค้ามา เราขอยืนยันรายการสั่งซื้อของคุณ&nbsp;<strong>%FNAME% %LNAME%</strong>&nbsp;ตามรายละเอียดด้านล่าง<br>\r\n			&nbsp;\r\n			<table class="movie_detail">\r\n				<tbody>\r\n					<tr>\r\n						<th>รหัสสินค้า</th>\r\n						<th>รายการ</th>\r\n						<th>จำนวน</th>\r\n						<th>ราคา</th>\r\n					</tr>\r\n					<tr>\r\n						<td>%DETAIL%</td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n			<br>\r\n			จำนวนเงิน <strong>%PRICE%</strong> %UNIT%<br>\r\n			ส่วนลด <strong>%DISCOUNT%</strong> %UNIT%<br>\r\n			น้ำหนัก <strong>%WEIGHT%</strong> กรัม<br>\r\n			ค่าขนส่ง <strong>%TRANSPORT%</strong> %UNIT%<br>\r\n			รวมเป็นจำนวนเงินทั้งสิ้น <strong>%TOTAL%</strong> %UNIT%<br>\r\n			<br>\r\n			กรุณาชำระเงิน และกลับมาแจ้งการชำระเงินได้ที่ <a href="%URL%" rel="nofollow">%URL%</a><br>\r\n			<br>\r\n			<strong>หมายเหตุ</strong> %COMMENT%<br>\r\n			<br>\r\n			ด้วยความขอบคุณ %WEBTITLE%</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="padding: 15px; color: rgb(153, 153, 153); font-family: tahoma; font-size: 8pt;">ข้อความนี้ถูกส่งมาโดยระบบอัตโนมัติเมื่อคุณได้ทำรายการกับเรา จากที่อยู่ IP : %IP% เมื่อ %TIME%</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n','1418899280','0');
INSERT INTO `{prefix}_emailtemplate` (`module`, `email_id`, `language`, `from_email`, `copy_to`, `name`, `subject`, `detail`, `last_update`, `last_send`) VALUES ('product','3','th','','','อีเมลแจ้งการชำระเงินจากผู้ซื้อ (3)','แจ้งชำระเงิน %WEBTITLE%','<div style="padding: 40px; margin-right: 40px; display: block; background-color: rgb(247, 247, 247);">\r\n	<table style="width: 100%; border-collapse: collapse;">\r\n		<tbody>\r\n			<tr>\r\n				<th style="border-width: 1px; border-style: none solid; border-color: rgb(59, 89, 152); padding: 5px; text-align: left; color: rgb(255, 255, 255); font-family: tahoma; font-size: 9pt; background-color: rgb(59, 89, 152);">\r\n					แจ้งการชำระเงิน</th>\r\n			</tr>\r\n			<tr>\r\n				<td style="border-width: 1px; border-style: none solid solid; border-color: rgb(204, 204, 204) rgb(204, 204, 204) rgb(59, 89, 152); padding: 15px; line-height: 1.8em; font-family: tahoma; font-size: 9pt;">\r\n					คุณ <strong>%FNAME% %LNAME%</strong><br />\r\n					<br />\r\n					ได้แจ้งการชำระเงินตามใบสั่งซื้อเลขที่ <strong>%ORDER%</strong><br />\r\n					เราได้รับแจ้งการทำรายการนี้แล้ว และจะรีบตรวจสอบและดำเนินการส่งสินค้าให้โดยเร็วที่สุด<br />\r\n					<br />\r\n					ด้วยความขอบคุณ <a href="%WEBURL%">%WEBTITLE%</a></td>\r\n			</tr>\r\n			<tr>\r\n				<td style="padding: 15px; color: rgb(153, 153, 153); font-family: tahoma; font-size: 8pt;">\r\n					ข้อความนี้ถูกส่งมาโดยระบบอัตโนมัติเมื่อคุณได้ทำรายการกับเรา จากที่อยู่ IP : %IP% เมื่อ %TIME%</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n','1353300373','0');
INSERT INTO `{prefix}_emailtemplate` (`module`, `email_id`, `language`, `from_email`, `copy_to`, `name`, `subject`, `detail`, `last_update`, `last_send`) VALUES ('product','4','th','','','อีเมลแจ้งการรับชำระเงินและตรวจสอบเรียบร้อย (4)','ยืนยันการชำระเงิน %WEBTITLE%','<style type="text/css">\r\ntable.movie_detail {\r\nmin-width: 800px;\r\nborder: 1px solid #666;\r\n}\r\ntable.movie_detail th {\r\npadding: 5px;\r\nbackground-color: #666;\r\ncolor:#FFF;\r\n}\r\ntable.movie_detail td{\r\npadding: 5px;\r\nbackground-color: #FFF;\r\ntext-align: center;\r\n}\r\n* {\r\nfont-family: Tahoma;\r\nfont-size: 12px;\r\n}\r\ntable.movie_detail td.left {\r\ntext-align: left;\r\n}\r\ntable.movie_detail td.right{\r\ntext-align: right;\r\n}</style>\r\n<div style="padding: 40px; margin-right: 40px; display: block; background-color: rgb(247, 247, 247);">\r\n	<table style="width: 100%; border-collapse: collapse;">\r\n		<tbody>\r\n			<tr>\r\n				<th style="border-width: 1px; border-style: none solid; border-color: rgb(59, 89, 152); padding: 5px; text-align: left; color: rgb(255, 255, 255); font-family: tahoma; font-size: 9pt; background-color: rgb(59, 89, 152);">\r\n					แจ้งรับชำระเงิน ตามใบสั่งซื่อเลขที่ %ORDER%</th>\r\n			</tr>\r\n			<tr>\r\n				<td style="border-width: 1px; border-style: none solid solid; border-color: rgb(204, 204, 204) rgb(204, 204, 204) rgb(59, 89, 152); padding: 15px; line-height: 1.8em; font-family: tahoma; font-size: 9pt;">\r\n					เรียนคุณ <strong>%FNAME% %LNAME%</strong><br />\r\n					<br />\r\n					เราได้รับชำระเงินจำนวน <strong>%PAID% %UNIT%</strong> และตรวจสอบการทำรายการของคุณเรียบร้อยแล้ว จะได้รีบจัดส่งสินค้าไปยังคุณโดยเร็วที่สุด<br />\r\n					<br />\r\n					ด้วยความขอบคุณ %WEBTITLE%<br />\r\n					<br />\r\n					หมายเหตุ %COMMENT%</td>\r\n			</tr>\r\n			<tr>\r\n				<td style="padding: 15px; color: rgb(153, 153, 153); font-family: tahoma; font-size: 8pt;">\r\n					ข้อความนี้ถูกส่งมาโดยระบบอัตโนมัติเมื่อคุณได้ทำรายการกับเรา จากที่อยู่ IP : %IP% เมื่อ %TIME%</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n','1353314832','0');
INSERT INTO `{prefix}_emailtemplate` (`module`, `email_id`, `language`, `from_email`, `copy_to`, `name`, `subject`, `detail`, `last_update`, `last_send`) VALUES ('product','6','th','','','อีเมลแจ้งการส่งสินค้า (6)','แจ้งส่งสินค้า %WEBTITLE%','<style type="text/css">table.movie_detail {\r\nmin-width: 800px;\r\nborder: 1px solid #666;\r\n}\r\ntable.movie_detail th {\r\npadding: 5px;\r\nbackground-color: #666;\r\ncolor:#FFF;\r\n}\r\ntable.movie_detail td{\r\npadding: 5px;\r\nbackground-color: #FFF;\r\ntext-align: center;\r\n}\r\n* {\r\nfont-family: Tahoma;\r\nfont-size: 12px;\r\n}\r\ntable.movie_detail td.left {\r\ntext-align: left;\r\n}\r\ntable.movie_detail td.right{\r\ntext-align: right;\r\n}\r\n</style>\r\n<div style="padding: 40px; margin-right: 40px; display: block; background-color: rgb(247, 247, 247);">\r\n<table style="width: 100%; border-collapse: collapse;">\r\n	<tbody>\r\n		<tr>\r\n			<th style="border-width: 1px; border-style: none solid; border-color: rgb(59, 89, 152); padding: 5px; text-align: left; color: rgb(255, 255, 255); font-family: tahoma; font-size: 9pt; background-color: rgb(59, 89, 152);">แจ้งส่งสินค้า เลขที่ %ORDER%</th>\r\n		</tr>\r\n		<tr>\r\n			<td style="border-width: 1px; border-style: none solid solid; border-color: rgb(204, 204, 204) rgb(204, 204, 204) rgb(59, 89, 152); padding: 15px; line-height: 1.8em; font-family: tahoma; font-size: 9pt;">เรียนคุณ <strong>%FNAME% %LNAME%</strong><br>\r\n			<br>\r\n			เราได้ดำเนินการส่งสินค้าตามใบสั่งซื้อของคุณเรียบร้อยแล้วตามใบส่งพัสดุเลขที่ <strong>%EMS%</strong><br>\r\n			&nbsp;\r\n			<table class="movie_detail">\r\n				<tbody>\r\n					<tr>\r\n						<th>รหัสสินค้า</th>\r\n						<th>รายการ</th>\r\n						<th>จำนวน</th>\r\n						<th>ราคา</th>\r\n					</tr>\r\n					<tr>\r\n						<td>%DETAIL%</td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n			<br>\r\n			คุณสามารถตรวจสอบสถานะการส่งสินค้าได้จาก http://track.thailandpost.co.th/trackinternet/Default.aspx<br>\r\n			<br>\r\n			With thank %WEBTITLE%<br>\r\n			<br>\r\n			หมายเหตุ %COMMENT%</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="padding: 15px; color: rgb(153, 153, 153); font-family: tahoma; font-size: 8pt;">ข้อความนี้ถูกส่งมาโดยระบบอัตโนมัติเมื่อคุณได้ทำรายการกับเรา จากที่อยู่ IP : %IP% เมื่อ %TIME%</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n','1418899293','0');
